import React, { Component } from "react";
import { Route } from "react-router-dom";
import { connect } from "react-redux";

import { createStructuredSelector } from "reselect";

import { fetchCollectionsStartAsync } from "../../redux/shop/shop.actions";
import { selectIsCollectionLoaded } from "../../redux/shop/shop.selectors";

import CollectionOverViewContainer from "./shop-page.cont";
import CollectionPage from "../collection/collection.com";
import WithSpinner from "../../components/with-spinner/with-spinner.com";

const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends Component {
  state = {
    loading: true,
  };

  unsubscribefromSnapShot = null;

  componentDidMount() {
    const { fetchCollectionsStartAsync } = this.props;
    fetchCollectionsStartAsync();
  }

  render() {
    const {
      match: { path },
      isCollectionLoaded,
    } = this.props;
    return (
      <div className="shop-page">
        <Route exact path={`${path}`} component={CollectionOverViewContainer} />
        <Route
          path={`${path}/:collectionID`}
          render={(props) => (
            <CollectionPageWithSpinner
              isLoading={!isCollectionLoaded}
              {...props}
            />
          )}
        />
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  isCollectionLoaded: selectIsCollectionLoaded,
});

const mapDispatchtoProps = (dispatch) => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync()),
});

export default connect(mapStateToProps, mapDispatchtoProps)(ShopPage);
