import React, { Component } from "react";
import { Route } from "react-router-dom";
import CollectionOverview from "../../components/collectionOverview/collection-overview.com";
import CollectionPage from "../collection/collection.com";
import { firestore } from "../../utils/firebase/firebase.utils";

class ShopPage extends Component {
  unsubscribefromSnapShot = null;
  componentDidMount() {
    const collectionRef = firestore.collection("collection");
  }
  render() {
    const {
      match: { path }
    } = this.props;
    return (
      <div className="shop-page">
        <Route exact path={`${path}`} component={CollectionOverview} />
        <Route path={`${path}/:collectionID`} component={CollectionPage} />
      </div>
    );
  }
}

export default ShopPage;
