import React, { Component } from "react";
import { Route } from "react-router-dom";
import { connect } from "react-redux";

import { updateColection } from "../../redux/shop/shop.actions";
import CollectionOverview from "../../components/collectionOverview/collection-overview.com";
import CollectionPage from "../collection/collection.com";
import { firestore } from "../../utils/firebase/firebase.utils";
import { convertCollectionsSnapshotToMap } from "../../utils/firebase/firestore.utils";
import WithSpinner from "../../components/with-spinner/with-spinner.com";

const CollectionOverViewWithSpinner = WithSpinner(CollectionOverview);

class ShopPage extends Component {
  state = {
    loading: true
  };

  unsubscribefromSnapShot = null;
  componentDidMount() {
    const { updateColection } = this.props;
    const collectionRef = firestore.collection("collection");

    this.unsubscribefromSnapShot = collectionRef.onSnapshot(async snapshot => {
      const collectionMap = convertCollectionsSnapshotToMap(snapshot);
      updateColection(collectionMap);
    });
  }
  render() {
    const {
      match: { path }
    } = this.props;
    return (
      <div className="shop-page">
        <Route exact path={`${path}`} component={CollectionOverview} />
        <Route path={`${path}/:collectionID`} component={CollectionPage} />
      </div>
    );
  }
}

const mapDispatchtoProps = dispatch => ({
  updateColection: collectionsMap => dispatch(updateColection(collectionsMap))
});

export default connect(null, mapDispatchtoProps)(ShopPage);
