import React, { useState, useEffect, useRef } from "react";
import HomaPage from "./pages/homepage/homepage-com";
import "./App.css";
import { Switch, Route } from "react-router-dom";
import ShopPage from "./pages/shop/shopage-com";
import Header from "./components/header/header.com";
import SignInSignUp from "./pages/sign-in-sign-up/sign-in-sign-up.com";
import { auth } from "./utils/firebase/firebase-utils";
import { crateUserProfileDocument } from "./utils/firebase/firestore-utils";
function App() {
  const [user, setUser] = useState(null);

  let unsubfromAuth = useRef(null);

  useEffect(() => {
    unsubfromAuth = auth.onAuthStateChanged(async user => {
      // setUser(user);
      crateUserProfileDocument(user);
      return function cleanup() {
        unsubfromAuth();
      };
    });
  });

  return (
    <div className="App">
      <Header currentUser={user} />
      <Switch>
        <Route exact path="/" component={HomaPage} />
        <Route path="/shop" component={ShopPage} />
        <Route path="/signin" component={SignInSignUp} />
      </Switch>
    </div>
  );
}

export default App;
