import React, { Component } from "react";
import { Route } from "react-router-dom";
import { connect } from "react-redux";

import { createStructuredSelector } from "reselect";

import { fetchCollectionsStartAsync } from "../../redux/shop/shop.actions";
import { selectIsCollectionsFetching } from "../../redux/shop/shop.selectors";

import CollectionOverview from "../../components/collectionOverview/collection-overview.com";
import CollectionPage from "../collection/collection.com";
import WithSpinner from "../../components/with-spinner/with-spinner.com";

const CollectionOverViewWithSpinner = WithSpinner(CollectionOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends Component {
  state = {
    loading: true,
  };

  unsubscribefromSnapShot = null;
  componentDidMount() {
    const { fetchCollectionsStartAsync } = this.props;
    fetchCollectionsStartAsync();
  }
  render() {
    const {
      match: { path },
      isCollectionsFetching,
    } = this.props;
    return (
      <div className="shop-page">
        <Route
          exact
          path={`${path}`}
          render={() => (
            <CollectionOverViewWithSpinner isLoading={isCollectionsFetching} />
          )}
        />
        <Route
          path={`${path}/:collectionID`}
          render={() => (
            <CollectionPageWithSpinner isLoading={isCollectionsFetching} />
          )}
        />
      </div>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  isCollectionsFetching: selectIsCollectionsFetching,
});

const mapDispatchtoProps = (dispatch) => ({
  fetchCollectionsStartAsync: () => dispatch(fetchCollectionsStartAsync()),
});

export default connect(null, mapDispatchtoProps)(ShopPage);
