import React, { Component } from "react";
import { Route } from "react-router-dom";
import { connect } from "react-redux";

import { createStructuredSelector } from "reselect";

import { fetchCollectionsStartAsync } from "../../redux/shop/shop.actions";

import CollectionOverview from "../../components/collectionOverview/collection-overview.com";
import CollectionPage from "../collection/collection.com";
import WithSpinner from "../../components/with-spinner/with-spinner.com";

const CollectionOverViewWithSpinner = WithSpinner(CollectionOverview);
const CollectionPageWithSpinner = WithSpinner(CollectionPage);

class ShopPage extends Component {
  state = {
    loading: true,
  };

  unsubscribefromSnapShot = null;
  componentDidMount() {}
  render() {
    const {
      match: { path },
    } = this.props;
    return (
      <div className="shop-page">
        <Route
          exact
          path={`${path}`}
          render={() => (
            <CollectionOverViewWithSpinner isLoading={this.state.loading} />
          )}
        />
        <Route
          path={`${path}/:collectionID`}
          render={() => (
            <CollectionPageWithSpinner isLoading={this.state.loading} />
          )}
        />
      </div>
    );
  }
}

const mapDispatchtoProps = (dispatch) => ({
  updateColection: (collectionsMap) =>
    dispatch(updateColection(collectionsMap)),
});

export default connect(null, mapDispatchtoProps)(ShopPage);
