import React from "react";
import { connect } from "react-redux";
import { ReactComponent as ShoppingIcon } from "../../assests/shopping-bag.svg";
import { toggleCartValue } from "../../redux/cart/cart.action";
import "./cart.sty.scss";

const Cart = ({ toggleCartValue, itemCount }) => {
  return (
    <div className="cart-icon" onClick={toggleCartValue}>
      <ShoppingIcon className="shopping-icon" />
      <span className="item-count">0</span>
    </div>
  );
};

const mapDispatchToProps = dispatch => ({
  toggleCartValue: () => dispatch(toggleCartValue())
});

const mapStateToProps = ({ cart: { cartItems } }) => ({
  itemCount: cartItems.reduce(
    (accumlatedQuantity, cartItem) => accumlatedQuantity + cartItem.quantity,
    0
  )
});

export default connect(null, mapDispatchToProps)(Cart);
