import { createSelector } from "reselect";

const COLLECTION_ID_MAP = {
  hats: 1,
  sneakers: 2,
  jackets: 3,
  womens: 4,
  mens: 5
};

const selectShop = state => state.shop;

export const selectCollection = createSelector(
  [selectShop],
  shop => shop.collection
);

//naming is incorrect here it must be collections instead of collection

export const selectCollec = collectionUrlParrm =>
  createSelector([selectCollection], collection =>
    collection.find(
      collection => collection.id == COLLECTION_ID_MAP[collectionUrlParrm]
    )
  );
