import React, { Component } from "react";
import FormInput from "../form-input/form-input.com";
import "./sign-in.sty.scss";
import CustomButton from "../custom-button/custom-button.com";
import * as firebase from "firebase";

// const firebaseConfig = {
//   apiKey: "AIzaSyDODRhCEMMmu3hZzfWrkiZPQaKuBxVjsjM",
//   authDomain: "estore-002.firebaseapp.com",
//   databaseURL: "https://estore-002.firebaseio.com",
//   projectId: "estore-002",
//   storageBucket: "estore-002.appspot.com",
//   messagingSenderId: "757875952831",
//   appId: "1:757875952831:web:12f4e79d044d3934df0a0a",
//   measurementId: "G-NDTYB2TYW8"
// };

// // Initialize Firebase
// var app = firebase.initializeApp(firebaseConfig);
class SignIn extends Component {
  state = { email: "", password: "" };

  handleSubmit = event => {
    event.preventDefault();
    firebase
      .auth()
      .signInWithEmailAndPassword(this.state.email, this.state.password)
      .catch(function(error) {
        // Handle Errors here.
        var errorCode = error.code;
        var errorMessage = error.message;
        // ...
      });
    this.setState({ email: "", password: "" });
  };

  onChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value });
  };

  render() {
    return (
      <div className="sign-in">
        <h1>SIGNIN</h1>
        <span>Sign in with your email and password</span>
        <form onSubmit={this.handleSubmit}>
          <FormInput
            name="email"
            type="email"
            value={this.state.email}
            required
            handleChange={this.onChange}
            label="Email"
          />
          <FormInput
            name="password"
            type="password"
            value={this.state.password}
            required
            handleChange={this.onChange}
            label="Password"
          />
          <CustomButton type="submit" value="Signin">
            Sign in
          </CustomButton>
        </form>
      </div>
    );
  }
}

export default SignIn;
