import React from "react";
import { connect } from "react-redux";
import {
  removeItemFromCart,
  addItemQuantity,
  decreseItemQuantity
} from "../../redux/cart/cart.action";
import "./check-out-item.sty.scss";

const CheckoutItem = ({
  cartItem: { id, name, imageUrl, price, quantity },
  removeItemFromCart,
  addItemQuantity,
  decreseItemQuantity
}) => (
  <div className="checkout-item">
    <div className="image-container">
      <img src={imageUrl} alt="item" />
    </div>
    <span className="name">{name}</span>
    <span className="quantity">
      <div className="arrow" onClick={() => removeItem(cartItem)}>
        &#10094;
      </div>
      <span className="value">{quantity}</span>
      <div className="arrow" onClick={() => addItem(cartItem)}>
        &#10095;
      </div>
    </span>
    <span className="price">{price}</span>
    <div className="remove-button" onClick={() => removeItemFromCart(cartItem)}>
      &#10005;
    </div>
  </div>
);

const mapDispatchToProps = dispatch => ({
  removeItemFromCart: itemId => dispatch(removeItemFromCart(itemId)),
  addItemQuantity: item => dispatch(addItemQuantity(item)),
  decreseItemQuantity: item => dispatch(decreseItemQuantity(item))
});

export default connect(null, mapDispatchToProps)(CheckoutItem);
