import React, { Component } from "react";
import { connect } from "react-redux";

import FormInput from "../form-input/form-input.com";
import "./sign-in.sty.scss";
import CustomButton from "../custom-button/custom-button.com";
import { signinWithGoogle } from "../../utils/firebase/firebase.utils";
import { auth } from "../../utils/firebase/firebase.utils";

import { googleSignInStart } from "../../redux/user/user.actions";

class SignIn extends Component {
  state = { email: "", password: "" };

  handleSubmit = async (event) => {
    event.preventDefault();
    const { email, password } = this.state;
    try {
      await auth.signInWithEmailAndPassword(email, password);
      this.setState({
        email: "",
        password: "",
      });
    } catch (error) {
      console.log(error);
    }
  };

  onChange = ({ target }) => {
    const { name, value } = target;
    this.setState({ [name]: value });
  };

  render() {
    const { googleSignInStart } = this.props;
    return (
      <div className="sign-in">
        <h1>SIGNIN</h1>
        <span>Sign in with your email and password</span>
        <form onSubmit={this.handleSubmit}>
          <FormInput
            name="email"
            type="email"
            value={this.state.email}
            required
            handleChange={this.onChange}
            label="Email"
          />
          <FormInput
            name="password"
            type="password"
            value={this.state.password}
            required
            handleChange={this.onChange}
            label="Password"
          />
          <CustomButton type="submit" value="Signin">
            Sign in
          </CustomButton>
          <CustomButton onClick={signinWithGoogle} isGoogleSignIn>
            Sign in With Google
          </CustomButton>
        </form>
      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => ({
  googleSignInStart: () => dispatch(googleSignInStart()),
});

export default connect()(SignIn);
