import React, { Component } from "react";
import HomaPage from "./pages/homepage/homepage.com";
import { Switch, Route, Redirect } from "react-router-dom";

import "./App.css";

import ShopPage from "./pages/shop/shopage.com";
import Header from "./components/header/header.com";
import SignInSignUp from "./pages/sign-in-sign-up/sign-in-sign-up.com";
import { auth } from "./utils/firebase/firebase.utils";
import {
  createUserProfileDocument,
  addCollectionAndDocuments
} from "./utils/firebase/firestore.utils";

import { selectCollectionForPreview } from "./redux/shop/shop.selectors";
import { setCurrentUser } from "./redux/user/user.actions";
import { connect } from "react-redux";
import CheckOutPage from "./pages/checkOutItem/check-out-page.com";

class App extends Component {
  unsubfromAuth = null;

  componentDidMount() {
    const { setCurrentUser, collection } = this.props;

    this.unsubfromAuth = auth.onAuthStateChanged(async userAuth => {
      if (userAuth) {
        const userRef = await createUserProfileDocument(userAuth);
        userRef.onSnapshot(snapshot => {
          setCurrentUser({
            id: snapshot.id,
            ...snapshot.data()
          });
        });
      } else {
        setCurrentUser(userAuth);
        console.log(collectionsArray);

        // addCollectionAndDocuments("collection", collectionsArray);
      }
    });
  }

  componentWillUnmount() {
    this.unsubfromAuth();
  }

  render() {
    return (
      <div className="App">
        <Header />
        <Switch>
          <Route exact path="/" component={HomaPage} />
          <Route path="/shop" component={ShopPage} />
          <Route exact path="/checkout" component={CheckOutPage} />
          <Route
            exact
            path="/signin"
            render={() =>
              this.props.currentUser ? <Redirect to="/" /> : <SignInSignUp />
            }
          />
        </Switch>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  currentUser: state.user.currentUser,
  collection: selectCollectionForPreview
});

const mapDispatchToProps = dispatch => ({
  setCurrentUser: user => dispatch(setCurrentUser(user))
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
